import React, { Component } from 'react'
import Select from 'react-select'
import axios from 'axios'
import { Link } from 'react-router-dom'

export default class Resp extends Component{
  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      dep:'',
      sems:'',
      Reg:'',
      dept:[],
      sem:[],
      i:0,
    }
  }
  render() {
    // console.log(this.state.selectOptions)
    return (
        <div id="di">
           <div id="tit" style={{marginBottom:'30px'}}>
           <h4 style={{marginBottom:'20px'}}>Student Name : <span>{thi["Name"]}</span></h4>
           <h4 style={{marginBottom:'20px'}}>Student Registration Number : <span>{x["Reg"]}</span></h4>
           <h4 style={{marginBottom:'20px'}}>Student Department : <span>{x["Dept"]}</span></h4>
           </div>
           <table className="table table-bordered table-striped table-hover">
           <thead className="thead-light">
               <tr>
               <th scope="col">Subject Name</th>
               <th scope="col">Grade</th>
               <th scope="col">Semister</th>
               </tr>
           </thead>
           <tbody>
           {
                       sub.map((item)=>{
                         return(
                           <tr key={item}>
                             <td>{item.sub}</td>
                             <td>{item.grade}</td>
                             <td>{el}</td>
                           </tr>
                         )
                       })
           }
           </tbody>
           </table>
            </div>
    )}
}
