            method:'POST',
            headers: {
                 'Content-Type' : 'application/json'
            },
            body: JSON.stringify({
                lreg,
                lpassword
            })
        })

        const data = await res.json()
        if(data.status === 'ok'){
            x.push(data.data);
            localStorage.setItem('profile',JSON.stringify(x[0]));
            localStorage.setItem('reg',lreg);
            setlreg("");
            setlpassword("");
            setx([]);
            navigate("/mainstud");
        }
        else{
            alert(data.message);
        }
    }

