import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';

const ManageSubjects = () => {
    const [dat,setdata] = useState([]);
    const [r,setr] = useState(-1);

    const handleedit = (i) => {
        alert(i);
    }

    const Ress = (props) => {
        console.log(props.da)
        var i=0;
        return(
            props.da.map((item)=>{
                i++;
                return(
                  <div key={i}>
                  <tr>
                    <td>{i}</td>
                    <td>{item.Subject}</td>
                    <td>{item.Code}</td>
                  </tr>
                  </div>
                )
            })
        )
    }

    useEffect(() => {
        const sublist = async() => {
    
            const res = await fetch('https://vignanminiproject.herokuapp.com/sub',{
                method:'POST',
                headers: {
                     'Content-Type' : 'application/json'
                },
            })
    
            const data = await res.json()
            if(data.status === 'ok'){
               setdata(data.data);
            }
            else{
                alert(data.message);
            }
        }
        setTimeout(() => {
            sublist();
          }, 1000);

        //   console.log("hello");
    },[]);

  return (
    <div id="bd">
    <div id="stad" className="shadow-sm p-3 mb-5 bg-white rounded">
    <h1>Manage Subjects</h1>
    <div id="head">
    <h3>View Subjects Info</h3>
    </div>
    <table class="table">
  <thead class="thead-dark">
    <tr>
      <th scope="col">#</th>
      <th scope="col">Subject Name</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td>Larry</td>
      <td>the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>
  </div>
  </div>
    // <div>
    //     <h1>Manage Subjects</h1>
    //     <h2>View Subjects info</h2>
    //     <table>
    //         <thead>
    //             <tr>
    //             <th>#</th>
    //             <th>Subject Name</th>
    //             <th>Subject Code</th>
    //             </tr>
    //         </thead>
    //         <Ress da={dat}/>
    //         <Link to="/editinfo">Edit</Link>
    //     </table>
    // </div>
  )
}

export default ManageSubjects
