import { render } from 'react-dom';
// import './index.css';
import * as React from 'react';
import { ScheduleComponent, ViewsDirective, ViewDirective, MonthAgenda, Inject } from '@syncfusion/ej2-react-schedule';
import { extend } from '@syncfusion/ej2-base';
import { SampleBase } from './sample-base';

export default class Calendar extends SampleBase {
  constructor(props){
    super(props)
    this.state = {
      selectOptions : []
    }
  }
  async getOptions(){
    const depths = ['CSE','ECE','EEE','CIVIL','MECH','ECM','IT']
    const sem = ['1-1','1-2','2-1','2-2','3-1','3-2','4-1','4-2']
    const grade = ['O','A','B','C','D','E','F']

    const gra = grade.map(d=>({
      "value":d,
      "label":d,
    }))

    const depts = depths.map(d=>({
        "value":d,
        "label":d
    }))

    const sems = sem.map(d=>({
        "value":d,
        "label":d
    }))
    this.setState({grad:gra,dept:depts,sem:sems})
  }
  componentDidMount(){
    this.getOptions()
  }
    data = extend([], dataSource.fifaEventsData, null, true);
    render() {
        return (<div className='schedule-control-section'>
        <div className='col-lg-12 control-section'>
          <div className='control-wrapper schedule-wrapper'>
            <ScheduleComponent width='100%' height='510px' selectedDate={new Date(2021, 5, 20)} eventSettings={{ dataSource: this.data }}>
              <ViewsDirective>
                <ViewDirective option='MonthAgenda'/>
              </ViewsDirective>
              <Inject services={[MonthAgenda]}/>
            </ScheduleComponent>
          </div>
        </div>
      </div>);
    }
}
