import React, { Component } from 'react'
import Select from 'react-select'
import axios from 'axios'

export default class AddStudent extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      dep:'',
      gender:'',
      name:'',
      roll:'',
      email:'',
      dob:'',
      phn:'',
      add:'',
      dept:[],
      gen:[]
    }
  }

 async getOptions(){
    const depths = ['CSE','ECE','EEE','CIVIL','MECH','ECM','IT']
    const gen = ['Male','Female']

    const depts = depths.map(d=>({
        "value":d,
        "label":d
    }))

    const gend = gen.map(d=>({
        "value":d,
        "label":d
    }))

    this.setState({dept:depts,gen:gend})

  }

  handlename(e){
    this.setState({name:e.value})
   }
   handleroll(e){
    this.setState({roll:e.value})
   }
   handlephn(e){
    this.setState({phn:e.value})
   }
   handleadd(e){
    this.setState({add:e.value})
   }
   handleemail(e){
    this.setState({email:e.value})
   }
   handledob(e){
    this.setState({dob:e.value})
   }

  handlegend(e){
    this.setState({gender:e.value})
   }

  handledept(e){
    this.setState({dep:e.value})
   }

   handlesubmit(e){
       e.preventDefault();
      
       const depart = this.state.dep;
       

       axios.post('http://localhost:3001/addsub',{
        // depart,
        // semister
       }).then(res=>this.setState({selectOptions:res.data.data})).catch(err=> console.log(err))
   }

   

  componentDidMount(){
      this.getOptions()
  }

  render() {
    // console.log(this.state.selectOptions)
    return (
      <div>
        <h1>Student Admission</h1>
        <h2>Fill the Student info</h2>
        <form >
        <label htmlFor="name">Full Name:</label>
        <input type="text" id="name" value={this.state.name} onChange={this.handlename}/>
        <label htmlFor="roll">Registration Number:</label>
        <input type="text" id="roll" value={this.state.roll} onChange={this.handleroll}/>
        <label htmlFor="ema">Email id:</label>
        <input type="email"  id="ema" value={this.state.email} onChange={this.handleemail} />
        <label htmlFor="gen">Gender:</label>
        <Select id="gen" options={this.state.gen} onChange={this.handlegend.bind(this)} />
        <label htmlFor="dept">Select Department:</label>
        <Select id="dept" options={this.state.dept} onChange={this.handledept.bind(this)} />
        <label htmlFor="date">DOB</label>
        <input type="date" id="date" value={this.state.dob} onChange={this.handledob} />
        <label htmlFor="phn">Phone number</label>
        <input type="text" id="phn" value={this.state.phn} onChange={this.handlephn}/>
        <label htmlFor="address">Address</label>
        <input type="text" id="address" value={this.state.add} onChange={this.handleadd} />
        <button onClick={this.handlesubmit.bind(this)}>Add</button>
        </form>
      </div>
    )
  }
}
