import React, { useEffect, useState } from 'react'

const ManageSubjects = () => {
    const [dat,setdata] = useState([]);
    const [r,setr] = useState(-1);

    const handleedit = (i) 

    const Ress = (props) => {
        // console.log(props.da)
        var i=0;
        return(
            props.da.map((item)=>{
                i++;
                return(
                  <div key="item">
                  <tr>
                    <td>{i}</td>
                    <td>{item.Subject}</td>
                    <td>{item.Code}</td>
                    <td><a href="#" onClick={handleedit(i)}></a></td>
                  </tr>
                  </div>
                )
            })
        )
    }

    useEffect(() => {
        const sublist = async() => {
    
            const res = await fetch('http://localhost:3001/sub',{
                method:'POST',
                headers: {
                     'Content-Type' : 'application/json'
                },
            })
    
            const data = await res.json()
            if(data.status === 'ok'){
            //     console.log("successful");
            //    console.log(data.data);
               setdata(data.data);
            }
            else{
                alert(data.message);
            }
        }
        sublist();
    });

  return (
    <div>
        <h1>Manage Subjects</h1>
        <h2>View Subjects info</h2>
        <table>
            <thead>
                <tr>
                <th>#</th>
                <th>Subject Name</th>
                <th>Subject Code</th>
                <th>Action</th>
                </tr>
            </thead>
            <Ress da={dat}/>
        </table>
    </div>
  )
}

export default ManageSubjects
