import React from 'react'
import "../css/Forms.css"
import { useState } from 'react'
import { Navigate ,useNavigate} from 'react-router-dom'




const Form1 = () => {
    const [reg,setreg] = useState("");
    const [pass,setpass] = useState("");
    const [password,setpassword] = useState("");
    const [lreg,setlreg] = useState("");
    const [lpassword,setlpassword] = useState("");
    const navigate = useNavigate();
    const [te,sette] =  useState(localStorage.getItem('tid'));

    const toggleForm1 = () => {
        var container = document.querySelector('.container2');
        container.classList.toggle('active')
    }

    const handlesubmit1 = async(e) => {
        e.preventDefault();

        // console.log(reg,pass,password);

        const res = await fetch('https://vignancalendar.herokuapp.com/passchange',{
            method:'POST',
            headers: {
                 'Content-Type' : 'application/json'
            },
            body: JSON.stringify({
                reg,
                pass,
                password
            })
        })

        const data = await res.json()
        if(data.status === 'ok'){
            setreg("");
            setpassword("");
            setpass("");
            alert(data.message);
        }
        else{
            alert(data.message);
        }
    }

    const handlesubmit = async(e) => {
        e.preventDefault();

        // console.log(lreg,lpassword);

        const res = await fetch('https://vignancalendar.herokuapp.com/stafflogin',{
            method:'POST',
            headers: {
                 'Content-Type' : 'application/json'
            },
            body: JSON.stringify({
                lreg,
                lpassword
            })
        })

        const data = await res.json()
        if(data.status === 'ok'){
            setlreg("");
            setlpassword("");
            localStorage.setItem('tid',lreg);
            navigate("/mainteach");
        }
        else{
            alert(data.message);
        }
    }

    

  return ( te ?  <Navigate to ="/mainteach" /> : <Tmain/>)

    
  
}

export default Form1
