import {useState} from 'react'
import React from "react";
import * as XLSX from "xlsx";
import { Navigate ,useNavigate} from 'react-router-dom'
import { Link } from 'react-router-dom';

const StudentDet = () => {
  const [files,setfiles] = useState();
  const [te,sette] =  useState(localStorage.getItem('tid'));

  const onChange = (e) => {
    const [file] = e.target.files;
    const reader = new FileReader();

    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: "binary" });
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      const data = XLSX.utils.sheet_to_json(ws);
      setfiles(data);
    };
    reader.readAsBinaryString(file);
  };

  const handleSubmit = async(e) => {

    e.preventDefault();

    console.log(files);
    const res = await fetch('https://vignanminiproject.herokuapp.com/excel',{
        method:'POST',
        headers: {
             'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            files
        })
    })

    const data = await res.json()
    if(data.status === 'ok'){
        alert("successful");
    }
    else{
        alert(data.error)
    }
    
}

const Func = () => {
  return (
    <div>
        <nav className="navbar navbar-expand-lg bg-dark navbar-dark">
        <div class="container-fluid">
          <Link class="navbar-brand" to="/mainstud">Student Results</Link>
          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarSupportedContent">
            <ul class="navbar-nav me-auto mb-2 mb-lg-0">
              <li class="nav-item">
                <Link class="nav-link active" aria-current="page" to="/mainstud">Home</Link>
              </li>
              <li class="nav-item">
                <Link class="nav-link active" aria-current="page" to="/studentres">Your Results</Link>
              </li>
              <li class="nav-item">
                <Link class="nav-link active" aria-current="page" to="/profile">Your Profile</Link>
              </li>
  
            </ul>
          </div>
        </div>
        </nav>
    <section className="user1" id="user1">
        <div className="container2">
            <div className="user signinBx">
                <div className="imgBx">
                    <lottie-player src="https://assets10.lottiefiles.com/packages/lf20_h9rxcjpi.json"  background="transparent"  speed="1"  style={{width: "100%" , height: "100%"}}  loop  autoplay></lottie-player>
                </div>
                <div className="formBx">
                    <form onSubmit={handleSubmit}>
                    <h2>Student Details</h2>
                    <label htmlFor="file" id="lab">Add file</label>
                    <input type="file" id="file" onChange={onChange} style={{display : 'none'}}/>
                    <input type="submit"  />
                    </form>
                </div>
            </div>
        </div>
    </section>
  </div>
  )
}

return ( te ? <Func/> : <Navigate to ="/" />)
}

export default StudentDet
