import React from 'react';
import {useState} from 'react';

const Events = () => {
  const [Subject,setsubject] = useState('');
  const [StartTime,setstart] = useState('');
  const [Location,setloc] = useState('');
  const [EndTime,setend] = useState('');


  const handlesubmit = async(e) => {
    e.preventDefault();

    const res = await fetch('https://vignancalendar.herokuapp.com/event',{
            method:'POST',
            headers: {
                 'Content-Type' : 'application/json'
            },
            body: JSON.stringify({
                Subject,
                StartTime
            })
        })

        const data = await res.json()
        if(data.status === 'ok'){
            setsubject('');
            setstart('');
            alert("successful");
        }
        else{
            alert(data.message);
        }
  }
  
  return (
    <section className="user1" id="user1">
        <div className="container2">
            <div className="user signinBx">
                <div className="imgBx">
                    <lottie-player src="https://assets10.lottiefiles.com/packages/lf20_h9rxcjpi.json"  background="transparent"  speed="1"  style={{width: "100%" , height: "100%"}}  loop  autoplay></lottie-player>
                </div>
                <div className="formBx">
                    <form onSubmit={handlesubmit}>
                    <h2>Event Details</h2>
                    <input type="text" id="subject" value={Subject} onChange={event => setsubject(event.target.value)} placeholder="Enter Title"/>
                    <input type="text" id="subject" value={Location} onChange={event => setloc(event.target.value)} placeholder="Enter Location"/>
                    <input type="datetime-local"  id="start" value={StartTime} onChange={event => setstart(String(event.target.value))}  />
                    <input type="datetime-local"  id="start" value={EndTime} onChange={event => set(String(event.target.value))}  />
                    <input type="submit" value="submit" />
                    </form>
                </div>
            </div>
        </div>
    </section>
  )
}

export default Events
