import React, { useEffect, useState ,Fragment} from 'react'


const CombinationSubject = () => {
    const [dat,setdata] = useState([]);
    const [dept,setdept] = useState("");
    const [sem,setsem] = useState("");
    const [sub,setsub] = useState("");

    

    useEffect(() => {
        const sublist = async() => {
    
            const res = await fetch('http://localhost:3001/sub',{
                method:'POST',
                headers: {
                     'Content-Type' : 'application/json'
                },
               
            })
    
            const data = await res.json()
            if(data.status === 'ok'){
               setdata(data.data);
            }
            else{
                alert(data.message);
            }
        }
        setTimeout(() => {
            sublist();
          }, 1000);

        //   console.log("hello");
    },[]);

    const handlesubmit = async(e) => {
        // e.preventDefaults();
        console.log(dept,res,sub);
        const res = await fetch('http://localhost:3001/mansub',{
                method:'POST',
                headers: {
                     'Content-Type' : 'application/json'
                },
                body: JSON.stringify({
                    dept,
                    sem,
                    sub
                })
            })
    
            const data = await res.json()
            if(data.status === 'ok'){
               setdata(data.data);
               alert(data.message);
            }
            else{
                alert(data.message);
            }
    }

  return (
    <div>
        <h1>Add Subject Combination</h1>
        <h2>Add Subject Combination</h2>
        <form >
            <label htmlFor="dept">Select Department</label>
            <select name="" id="dept" value={dept} onChange={event => setdept(String(event.target.value))}>
                <option value="CSE">CSE</option>
                <option value="ECE">ECE</option>
                <option value="CIVIL">CIVIL</option>
                <option value="MECH">MECH</option>
                <option value="EEE">EEE</option>
                <option value="IT">IT</option>
                <option value="ECM">ECM</option>
            </select>
            <label htmlFor="semister">Select semister</label>
            <select name="" id="semister" value={sem} onChange={event => setsem(String(event.target.value))}>
                <option value="1-1">1-1</option>
                <option value="1-2">1-2</option>
                <option value="2-1">2-1</option>
                <option value="2-2">2-2</option>
                <option value="3-1">3-1</option>
                <option value="3-2">3-2</option>
                <option value="4-1">4-1</option>
                <option value="4-2">4-2</option>
            </select>
            <Ress da={dat}/>
            <button onClick={handlesubmit}>Submit</button>
        </form>
    </div>
  )
}

export default CombinationSubject
