import React, { Component } from 'react'
import Select from 'react-select'
import axios from 'axios'
import { Link } from 'react-router-dom'

export default class SubjectList extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      dep:'',
      sems:'',
      dept:[],
      sem:[],
      i:0,
    }
  }

 async getOptions(){
    const depths = ['CSE','ECE','EEE','CIVIL','MECH','ECM','IT']
    const sem = ['1-1','1-2','2-1','2-2','3-1','3-2','4-1','4-2']

    const depts = depths.map(d=>({
        "value":d,
        "label":d
    }))

    const sems = sem.map(d=>({
        "value":d,
        "label":d
    }))
    this.setState({dept:depts,sem:sems})

  }



  handledept(e){
    this.setState({dep:e.value})
   }

   handlesem(e){
    this.setState({sems:e.value})
   }

   handlesubmit(e){
       e.preventDefault();
      
       const depart = this.state.dep;
       const semister = this.state.sems;

       axios.post('https://vignanminiproject.herokuapp.com/findsub',{
        depart,
        semister
       }).then(res=>this.setState({selectOptions:res.data.data})).catch(err=> console.log(err))
   }

   

  componentDidMount(){
      this.getOptions()
  }

  render() {
    // console.log(this.state.selectOptions)
    return (
      <div>
        <nav className="navbar navbar-expand-lg bg-dark navbar-dark" style={{margin:'0px'}}>
//         <div class="container-fluid">
//           <Link style={{textDecoration:'none'}} class="navbar-brand" to="/mainstud">Student Results</Link>
//           <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
//             <span class="navbar-toggler-icon"></span>
//           </button>
//           <div class="collapse navbar-collapse" id="navbarSupportedContent">
//             <ul class="navbar-nav me-auto mb-2 mb-lg-0">
//               <li class="nav-item">
//                 <Link style={{textDecoration:'none'}} class="nav-link active" aria-current="page" to="/mainstud">Home</Link>
//               </li>
//               <li class="nav-item">
                 <Link style={{textDecoration:'none'}} class="nav-link active" aria-current="page" to="/studentres">Your Results</Link>
               </li>
               <li class="nav-item">
                 <Link style={{textDecoration:'none'}} class="nav-link active" aria-current="page" to="/profile">Your Profile</Link>
               </li>
             </ul>
           </div>
         </div>
       </nav>
        <div id="bd">
      <div id="stad" className="shadow-sm p-3 mb-5 bg-white rounded">
