import React, { useState } from 'react'
import { Navigate} from 'react-router-dom'
// import '../css/Results.css'
import { Link } from 'react-router-dom';
import { handleInputChange } from 'react-select/dist/declarations/src/utils';



const StudentRes = () => {
  const [el,sete] = useState("1-1");
  const [te,sette] = useState("");
  const [x,setx] = useState([]);
  const [sub,setsub] = useState([]);
  const [back,setback] = useState([]);
  const [val,setval] = useState(false);
  const [v,setv] = useState(localStorage.getItem('profile'));
  const [xi,setxi] = useState([]);

  const handleSubmit = async(e) => {
    e.preventDefault();
    // console.log(reg,dept,year);


    const res = await fetch('http://localhost:3001/gets',{
        method:'POST',
        headers: {
             'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            el,
            te
        })
    })
    const data = await res.json();

    if(data.status === 'ok' && data.message === 'Successful'){
        setx(data.data);
        setsub(data.data["subject"])
        setval(true);
        // console.log(x["subject"]);
        // alert("successful");
    }
    else if(data.status === 'ok' && data.message === 'backlogs')
    {
      setback(data.data);
      setsub([])
      setx([])
      setval(false)
      // console.log(back);
      // back.map((item)=>{
      //   console.log(item);
      // })
    }
    else{
        alert(data.message);
        setsub([])
        setx([])
        setback([])
        setval(false)
    }
  }

  const Resp = () => {
    if(val === true)
    {
      if(x)
      {
        return(
          <div id="di">
          <div id="tit" style={{marginBottom:'30px'}}>
          <h4 style={{marginBottom:'20px'}}>Student Name : <span>{x["Name"]}</span></h4>
