import React, { useEffect, useState } from 'react'

const CombinationSubject = () => {
    
    const Ress = (props) => {
        console.log(props.da)
        var i=0;
        return(
            props.da.map((item)=>{
                i++;
                return(
                  <div key={i}>
                  <tr>
                    <td>{i}</td>
                    <td>{item.Subject}</td>
                    <td>{item.Code}</td>
                  </tr>
                  </div>
                )
            })
        )
    }

    useEffect(() => {
        const sublist = async() => {
    
            const res = await fetch('http://localhost:3001/subjects',{
                method:'POST',
                headers: {
                     'Content-Type' : 'application/json'
                },
            })
    
            const data = await res.json()
            if(data.status === 'ok'){
               setdata(data.data);
            }
            else{
                alert(data.message);
            }
        }
        setTimeout(() => {
            sublist();
          }, 1000);

        //   console.log("hello");
    },[]);

  return (
    <div>
        <h1>Add Subject Combination</h1>
        <h2>Add Subject Combination</h2>
        <form onSubmit={handlesubmit}>
            <label htmlFor="dept">Select Department</label>
            <select name="" id="dept">
                <option value="CSE">CSE</option>
                <option value="ECE">ECE</option>
                <option value="CIVIL">CIVIL</option>
                <option value="MECH">MECH</option>
                <option value="EEE">EEE</option>
                <option value="IT">IT</option>
                <option value="ECM">ECM</option>
            </select>
            <label htmlFor="semister">Select semister</label>
            <select name="" id="semister">
                <option value="1-1">1-1</option>
                <option value="1-2">1-2</option>
                <option value="2-1">2-1</option>
                <option value="2-2">2-2</option>
                <option value="3-1">3-1</option>
                <option value="3-2">3-2</option>
                <option value="4-1">4-1</option>
                <option value="4-2">4-2</option>
            </select>
        </form>
    </div>
  )
}

export default CombinationSubject
