import React, { Component } from 'react'
import Select from 'react-select'
import axios from 'axios'

export default class CombinationSubject extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      id: "",
      name: '',
      dept:[],
      sem:'',
    }
  }

 async getOptions(){
    const res = await axios.post('http://localhost:3001/sub')
    const data = res.data.data

    const options = data.map(d => ({
      "value" : d.Subject,
      "label" : d.Subject

    }))

    const depts = [
        {
            "value" : "CSE",
            "label" : "CSE"
        },
        {
            "value" : "ECE",
            "label" : "ECE"
        },
        {
            "value" : "EEE",
            "label" : "EEE"
        },
        {
            "value" : "ECM",
            "label" : "ECM"
        },
        {
            "value" : "IT",
            "label" : "IT"
        },
        {
            "value" : "MECH",
            "label" : "MECH"
        },
        {
            "value" : "CIVIL",
            "label" : "CIVIL"
        }
    ]

    this.setState({selectOptions: options,dept:depts})

  }


  handleChange(e){
   this.setState({id:e.value, name:e.label})
  }

  componentDidMount(){
      this.getOptions()
  }

  render() {
    console.log(this.state.selectOptions)
    return (
      <div>
        <h1>Add Subject Combination</h1>
        <h2>Add Subject Combination</h2>
        
        <Select options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
        <Select options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
        <Select options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
        <p>You have selected <strong>{this.state.name}</strong> whose id is <strong>{this.state.id}</strong></p>
        <p>You have selected <strong>{this.state.dept}</strong></p>
        <p>You have selected <strong>{this.state.sem}</strong></p>
      </div>
    )
  }
}
