import React, { useState } from "react";
import { FaUpload } from 'react-icons/fa';
import '../css/fil.css'

function File() {
  const [title,settitle] = useState("");
    const [file, setFile] = useState("");
    const [result, setResult] = useState({});
    const [loading, setLoading] = useState(false);
    const [v,setv] = useState(localStorage.getItem('reg'));

    var formdata = new FormData();
    formdata.append("file", file);

    var requestOptions = {
      method: 'POST',
      body: formdata,
      redirect: 'follow'
    };

  window.addEventListener('load', (event) => {
    //selecting all required elements
const dropArea = document.querySelector(".drag-area"),
dragText = dropArea.querySelector(".drg"),
button = dropArea.querySelector(".brw"),
input = dropArea.querySelector(".fill");
let file; //this is a global variable and we'll use it inside multiple functions

button.onclick = ()=>{
  input.click(); //if user click on the button then the input also clicked
}

input.addEventListener("change", function(){
  //getting user select file and [0] this means if user select multiple files then we'll select only the first one
  file = this.files[0];
  dropArea.classList.add("active");
  setFile();
//   showFile(); //calling function
});


//If user Drag File Over DropArea
dropArea.addEventListener("dragover", (event)=>{
  event.preventDefault(); //preventing from default behaviour
  dropArea.classList.add("active");
  dragText.textContent = "Release to Upload File";
});

//If user leave dragged File from DropArea
dropArea.addEventListener("dragleave", ()=>{
  dropArea.classList.remove("active");
  dragText.textContent = "Drag & Drop to Upload File";
});

//If user drop File on DropArea
dropArea.addEventListener("drop", (event)=>{
  event.preventDefault(); //preventing from default behaviour
  //getting user select file and [0] this means if user select multiple files then we'll select only the first one
  file = event.dataTransfer.files[0];
  setFile(file);
//   showFile(); //calling function
});


  });
    

    const handleUpload = (event) => {
      setFile(event.target.files[0]);
    }


    const handleSubmit = (e) => {
      e.preventDefault();

      console.log(title,file.type);
      // fetch("https://file.io", requestOptions)
      //   .then(response => response.json())
      //   .then(result => setResult(result.link))
      //   .then(() => setLoading(false))
      //   .catch(error => console.log('error', error));

      //   fetch('http://localhost:3001/fileupload',{
      //     method:'POST',
      //     headers: {
      //          'Content-Type' : 'application/json'
      //     },
      //     body: JSON.stringify({
      //         title,
      //         result,
      //         v
      //     })
      //   })
      //   .then(response => response.json())
      //   .then(result => alert(result.message))
      //   .catch(error => console.log('error', error));
    }


  return (
    <div className="fileupload">
      <form  action="#">
        <div class="drag-area">
            <h1 id="hel">File Upload</h1>
            <input type="text" name="text" id="txt" placeholder="Enter The Name Of The File" value={title} onChange={event => settitle(event.target.value)} />
            <div class="icon"><i class="fas fa-cloud-upload-alt"></i></div>
            <header class="drg">Drag & Drop to Upload File</header>
            <span>OR</span>
            <button class="brw">Browse File</button>
            <input type="file" class="fill" onChange={handleUpload}  />
            <button onClick={handleSubmit} type="submit" id="bt">Submit</button>
      </div>
    </form>
    </div>
  );

}


export default File;
