import React, { Component } from 'react'
import Select from 'react-select'
import axios from 'axios'

export default class AddStudent extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      dep:'',
      sems:'',
      gender:'',
      dept:[],
      sem:[],
      gen:[]
    }
  }

 async getOptions(){
    const depths = ['CSE','ECE','EEE','CIVIL','MECH','ECM','IT']
    const sem = ['1-1','1-2','2-1','2-2','3-1','3-2','4-1','4-2']
    const gen = ['Male','Female']

    const depts = depths.map(d=>({
        "value":d,
        "label":d
    }))

    const gend = gen.map(d=>({
        "value":d,
        "label":d
    }))

    const sems = sem.map(d=>({
        "value":d,
        "label":d
    }))
    this.setState({dept:depts,sem:sems,gen:gend})

  }

  handlegend(e){
    this.setState({gender:e.value})
   }

  handledept(e){
    this.setState({dep:e.value})
   }

   handlesem(e){
    this.setState({sems:e.value})
   }

   handlesubmit(e){
       e.preventDefault();
      
       const depart = this.state.dep;
       const semister = this.state.sems;

       axios.post('http://localhost:3001/findsub',{
        depart,
        semister
       }).then(res=>this.setState({selectOptions:res.data.data})).catch(err=> console.log(err))
   }

   

  componentDidMount(){
      this.getOptions()
  }

  render() {
    // console.log(this.state.selectOptions)
    return (
      <div>
        <h1>Student Admission</h1>
        <h2>Fill the Student info</h2>
        <form >
        <label htmlFor="name">Full Name:</label>
        <input type="text" id="name"/>
        <label htmlFor="roll">Registration Number:</label>
        <input type="text" id="roll"/>
        <label htmlFor="ema">Email id:</label>
        <input type="email"  id="ema" />
        <label htmlFor="gen">Gender:</label>
        <Select id="gen" options={this.state.gen} onChange={this.handlegend.bind(this)} />
        <label htmlFor="dept">Select Department:</label>
        <Select id="dept" options={this.state.dept} onChange={this.handledept.bind(this)} />
        <label htmlFor="date">DOB</label>
        <input type="date" id="date" />
        <input type="text" id="phn"/>
        <button onClick={this.handlesubmit.bind(this)}>Add</button>
        </form>
      </div>
    )
  }
}
