import React, { Component } from 'react'
import Select from 'react-select'
import axios from 'axios'
import { Link } from 'react-router-dom'
import '../css/studad.css';

export default class AddStudent extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      dep:'',
      gender:'',
      name:'',
      roll:'',
      email:'',
      dob:'',
      phn:'',
      add:'',
      year:'',
      dept:[],
      gen:[]
    }
  }

 async getOptions(){
    const depths = ['CSE','ECE','EEE','CIVIL','MECH','ECM','IT']
    const gen = ['Male','Female']

    const depts = depths.map(d=>({
        "value":d,
        "label":d
    }))

    const gend = gen.map(d=>({
        "value":d,
        "label":d
    }))

    this.setState({dept:depts,gen:gend})

  }

  handlename(e){
    this.setState({name:e.target.value})
   }
   handleroll(e){
    this.setState({roll:e.target.value})
   }
   handlephn(e){
    this.setState({phn:e.target.value})
   }
   handleadd(e){
    this.setState({add:e.target.value})
   }
   handleemail(e){
    this.setState({email:e.target.value})
   }
   handledob(e){
    this.setState({dob:e.target.value})
   }
   handleyear(e){
    this.setState({year:e.target.value})
   }

  handlegend(e){
    this.setState({gender:e.value})
   }

  handledept(e){
    this.setState({dep:e.value})
   }

   handlesubmit(e){
       e.preventDefault();
      
       const depart = this.state.dep;
       const name =this.state.name;
       const reg = this.state.roll;
       const dob = this.state.dob;
       const email = this.state.email;
       const phn = this.state.phn;
       const gender = this.state.gender;
       const address = this.state.add;
       const year = this.state.year;
       
       axios.post('https://vignanminiproject.herokuapp.com/addstud',{
        depart,
        name,
        reg,
        dob,
        email,
        phn,
        address,
        gender,
        year,
       }).then(res=>alert(res.data.message)).catch(err=> console.log(err))
   }

   

  componentDidMount(){
      this.getOptions()
  }

  render() {
    // console.log(this.state.selectOptions)
    return (
      <div>
        <nav className="navbar navbar-dark bg-dark" style={{margin:'0px'}}>
        <div class="container-fluid">
          <a class="navbar-brand" href="#">Student Results</a>
          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarSupportedContent">
            <ul class="navbar-nav me-auto mb-2 mb-lg-0">
              <li class="nav-item">
                <Link class="nav-link active" aria-current="page" to="/mainteach">Home</Link>
              </li>
              <li class="nav-item dropdown">
                <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                  Details
                </a>
                <ul class="dropdown-menu" aria-labelledby="navbarDropdown">
                  <li><Link class="dropdown-item" to="/addstudent">Student Details</Link></li>
                  <li><Link class="dropdown-item" to="/addresults">Student Results</Link></li>
                </ul>
              </li>
              <li class="nav-item dropdown">
                <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                  Subjects
                </a>
                <ul class="dropdown-menu" aria-labelledby="navbarDropdown">
                  <li><Link class="dropdown-item" to="/addsub">Add Subjects</Link></li>
                  <li><Link class="dropdown-item" to="/subinfo">Subject Details</Link></li>
                  <li><Link class="dropdown-item" to="/combination">Add / Delete SubjectCombo</Link></li>
                  <li><Link class="dropdown-item" to="/sublist">SubjectDept List</Link></li>
                </ul>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">Logout</a>
              </li>
            </ul>
            
          </div>
        </div>
        </nav>
      </div>
      
    )
  }
}
