import React, { useState } from 'react'

const StudentRes = () => {
  const [x,setx] = useState([]);
  const v = localStorage.getItem('reg');
  const handleSubmit = async(e,x) => {
    e.preventDef
    const res = await fetch('http://localhost:3001/gets',{
        method:'POST',
        headers: {
             'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            x,
            v
        })
    })
    const data = await res.json()
    if(data.status === 'ok'){
        setx(data.data);
        console.log(x);
        alert("successful");
    }
    else{
        alert(data.message);
    }
  }

  return (
    <div>StudentRes
      <form action="">
      <button type="submit" onSubmit={handleSubmit('1-1')} >1-1</button>
      <button type="submit" onSubmit={handleSubmit('1-2')} >1-2</button>
      <button type="submit" onSubmit={handleSubmit('2-1')} >2-1</button>
      <button type="submit" onSubmit={handleSubmit('2-2')}>2-2</button>
      <button type="submit" onSubmit={handleSubmit('3-1')}>3-1</button>
      <button type="submit" onSubmit={handleSubmit('3-2')}>3-2</button>
      <button type="submit" onSubmit={handleSubmit('4-1')}>4-1</button>
      <button type="submit" onSubmit={handleSubmit('4-2')}>4-2</button>
      </form>
      
    </div>
  )
}

export default StudentRes
