import React, { useState } from 'react'

const StudentRes = () => {
  const [x,setx] = useState([]);
  const v = localStorage.getItem('reg');
  const handleSubmit = async(e) => {
    // e.preventDefault();
    const d = e.target.value;
    console.log(d);

    const res = await fetch('http://localhost:3001/gets',{
        method:'POST',
        headers: {
             'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            d,
            v
        })
    })
    const data = await res.json();

    if(data.status === 'ok'){
        setx(data.data);
        console.log(x);
        alert("successful");
    }
    else{
        alert(data.message);
    }
  }

  return (
    <div>StudentRes
      <form onSubmit={handleSubmit}>
      <select>
        <option value="1-1">Grapefruit</option>
        <option value="1-2">Lime</option>
        <option value="2-1">Coconut</option>
        <option value="3">Mango</option>
      </select>
      </form>
      
    </div>
  )
}

export default StudentRes
