import React, { Component } from 'react'
import Select from 'react-select'
import axios from 'axios'

export default class CombinationSubject extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      id: "",
      name: ''
    }
  }

 async getOptions(){
    const res = await axios.post('http://localhost:3001/sub')
    const data = res.data.data

    // console.log(res);
    data.map((d)=>{
        console.log(d);
    })
    // console.log(data);

    const options = data.map(d => ({
      "value" : d.Subject,
      "label" : d.Subject

    }))

    this.setState({selectOptions: options})

  }

  handleChange(e){
   this.setState({id:e.value, name:e.label})
  }

  componentDidMount(){
      this.getOptions()
  }

  render() {
    console.log(this.state.selectOptions)
    return (
      <div>
          <h1>Add Subject Combination</h1>
        <h2>Add Subject Combination</h2>
        <form onSubmit={this.handlesubmit}>
            <label htmlFor="dept">Select Department</label>
            <select name="" id="dept" value={dept} onChange={event => setdept(String(event.target.value))}>
                <option value="CSE">CSE</option>
                <option value="ECE">ECE</option>
                <option value="CIVIL">CIVIL</option>
                <option value="MECH">MECH</option>
                <option value="EEE">EEE</option>
                <option value="IT">IT</option>
                <option value="ECM">ECM</option>
            </select>
            <label htmlFor="semister">Select semister</label>
            <select name="" id="semister" value={sem} onChange={event => setsem(String(event.target.value))}>
                <option value="1-1">1-1</option>
                <option value="1-2">1-2</option>
                <option value="2-1">2-1</option>
                <option value="2-2">2-2</option>
                <option value="3-1">3-1</option>
                <option value="3-2">3-2</option>
                <option value="4-1">4-1</option>
                <option value="4-2">4-2</option>
            </select>
            </form>
        <Select options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
        <p>You have selected <strong>{this.state.name}</strong> whose id is <strong>{this.state.id}</strong></p>
      </div>
    )
  }
}
