import React, { useState } from "react";
import { FaUpload } from 'react-icons/fa';
import '../css/fil.css'

function File() {
  window.addEventListener('load', (event) => {
    
  });
    const [title,settitle] = useState("");
    const [file, setFile] = useState("");
    const [result, setResult] = useState({});
    const [loading, setLoading] = useState(false);
    const [v,setv] = useState(localStorage.getItem('reg'));

    var formdata = new FormData();
    formdata.append("file", file);

    var requestOptions = {
      method: 'POST',
      body: formdata,
      redirect: 'follow'
    };

    const handleUpload = (event) => {
      setFile(event.target.files[0]);
    }


    const handleSubmit = (e) => {
      e.preventDefault();
      fetch("https://file.io", requestOptions)
        .then(response => response.json())
        .then(result => setResult(result.link))
        .then(() => setLoading(false))
        .catch(error => console.log('error', error));

        fetch('http://localhost:3001/fileupload',{
          method:'POST',
          headers: {
               'Content-Type' : 'application/json'
          },
          body: JSON.stringify({
              title,
              result,
              v
          })
        })
        .then(response => response.json())
        .then(result => alert(result.message))
        .catch(error => console.log('error', error));
    }


  return (
    <div className="fileupload">
      <form  action="#">
        <div class="drag-area">
            <h1 id="hel">File Upload</h1>
            <input type="text" name="text" id="txt" placeholder="Enter The Name Of The File" value={title} onChange={event => settitle(event.target.value)} />
            <div class="icon"><i class="fas fa-cloud-upload-alt"></i></div>
            <header>Drag & Drop to Upload File</header>
            <span>OR</span>
            <button>Browse File</button>
            <input type="file" onChange={handleUpload}  />
            <button onClick={handleSubmit} type="submit" id="bt">Submit</button>
      </div>
    </form>
    </div>
  );

}


export default File;
