import React from 'react'
import { useState } from 'react'
import { useNavigate} from 'react-router-dom'

const AddSubject = () => {
    const [subj,setsubj] = useState("");
    const [cod,setcod] = useState("");
    const handlesubmit = async(e) => {
        e.preventDefault();

        // console.log(lreg,lpassword);

        const res = await fetch('http://vignancalendar.herokuapp.c/stafflogin',{
            method:'POST',
            headers: {
                 'Content-Type' : 'application/json'
            },
            body: JSON.stringify({
                lreg,
                lpassword
            })
        })

        const data = await res.json()
        if(data.status === 'ok'){
            setlreg("");
            setlpassword("");
            localStorage.setItem('tid',lreg);
            navigate("/mainteach");
        }
        else{
            alert(data.message);
        }
    }

  return (
    <div>
        <h1>Subject Creation</h1>
        <h2>Create Subject</h2>
        <form onSubmit={handlesubmit}>
            <label htmlFor="sub">Subject Name:</label>
            <input type="text" name="" id="sub" value={subj} onChange={event => setsubj(event.target.value)}/>
            <label htmlFor="code">Subject Code:</label>
            <input type="text" name="" id="code" value={cod} onChange={event => setcod(event.target.value)}/>
            <input type="submit" value="" />
        </form>
    </div>
  )
}

export default AddSubject
