import { render } from 'react-dom';
// import './index.css';
import * as React from 'react';
import { ScheduleComponent, ViewsDirective, ViewDirective, MonthAgenda, Inject } from '@syncfusion/ej2-react-schedule';
import { extend } from '@syncfusion/ej2-base';
import { SampleBase } from './sample-base';

export default class Calendar extends SampleBase {
  constructor(props){
    super(props)
    this.state = {
      selectOptions : []
    }
  }
  async getOptions(){
    axios.post('https://vignanminiproject.herokuapp.com/findstudent',{
      year,
      depart
     }).then(res=>{
       if(res.data.message==="No Students")
       {
          // window.open("/addresults");
          alert(res.data.message);
       }
       else
       {
        const dep = res.data.data.map(d=>({
          "value":d.Reg,
          "label":d.Reg
          }))
         this.setState({roll:dep});
        }
     }).catch(err=> console.log(err))
  }
  componentDidMount(){
    this.getOptions()
  }
    // data = extend([], dataSource.fifaEventsData, null, true);
    render() {
        return (<div className='schedule-control-section'>
        <div className='col-lg-12 control-section'>
          <div className='control-wrapper schedule-wrapper'>
            <ScheduleComponent width='100%' height='510px' selectedDate={new Date(2021, 5, 20)} eventSettings={{ dataSource: this.state.selectOptions }}>
              <ViewsDirective>
                <ViewDirective option='MonthAgenda'/>
              </ViewsDirective>
              <Inject services={[MonthAgenda]}/>
            </ScheduleComponent>
          </div>
        </div>
      </div>);
    }
}
