import React, { useState } from 'react'
import { Navigate, useNavigate} from 'react-router-dom'
import '../css/Results.css'



const StudentRes = () => {
  const [el,sete] = useState("1-1");
  const [x,setx] = useState([]);
  const [sub,setsub] = useState([]);
  const [back,setback] = useState([]);
  const [val,setval] = useState(false);
  const [v,setv] = useState(localStorage.getItem('reg'));
  const handleSubmit = async(e) => {
    e.preventDefault();
    // console.log(v);

    const res = await fetch('https://vignanminiproject.herokuapp.com/gets',{
        method:'POST',
        headers: {
             'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            el,
            v
        })
    })
    const data = await res.json();

    if(data.status === 'ok' && data.message === 'Successful'){
        setx(data.data);
        setsub(data.data["subject"])
        setval(true);
        // console.log(x["subject"]);
        // alert("successful");
    }
    else if(data.status === 'ok' && data.message === 'backlogs')
    {
      setback(data.data);
      setsub([])
      setx([])
      setval(false)
      // console.log(back);
      // back.map((item)=>{
      //   console.log(item);
      // })
    }
    else{
        alert(data.message);
        setsub([])
        setx([])
        setback([])
        setval(false)
    }
  }

  const Resp = () => {
    if(val === true)
    {
      if(x)
      {
        return(
          <div className="main-wrapper">
            <div className="content-wrapper">
                <div className="content-container">
                    <div className="main-page">
                        <div className="container-fluid">
                            <div className="row page-title-div">
                                <div className="col-md-12">
                                    <h2 className="title" align="center">Result Management System</h2>
                                </div>
                            </div>
                        </div>
                        <section className="section" id="exampl">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-md-8 col-md-offset-2">
                                        <div className="panel">
                                            <div className="panel-heading">
                                                <div className="panel-title">
                                                    <h3 align="center">Student Result Details</h3>
                                                    <hr />
                                                    <p><b></b></p>
                                                    <p><b></b></p>
                                                    <p><b></b></p>
                                                    </div>
                                            <div className="panel-body p-20">
                                                <table className="table table-hover table-bordered" border="1" width="100%">
                                                <thead>
                                                        <tr style="text-align: center">
                                                            <th style="text-align: center">#</th>
                                                            <th style="text-align: center"> Subject</th>    
                                                            <th style="text-align: center">Marks</th>
                                                        </tr>
                                               </thead>
                                                	<tbody>
                                                    {
                                                   sub.map((item)=>{
                                                      return(
                                                        <div key="item">
                                                        <tr>
                                                        <th scope="row" style="text-align: center"></th>
                                                        <td style="text-align: center"></td>
                                                        <td style="text-align: center"></td>
                                                          <td>{item.sub}</td>
                                                          <td>{item.grade}</td>
                                                        </tr>
                                                        </div>
                                                      )
                                                    })
                                                    }
                                                    <tr>
                                                    
                                                		</tr>
                                                    <tr>
                                                    <td colspan="3" align="center"><i className="fa fa-print fa-2x" aria-hidden="true" style="cursor:pointer"  ></i></td>
                                                    </tr>
                                                  </tbody>
                                                  </table>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="form-group">
                                          <div className="col-sm-6">
                                          <a href="">Back to Home</a>
                                    </div>
                                    </div>
                                </div>
                            </div>
                            </div>                  
                        </section>
                    </div>
                </div> 
            </div>
          </div>    
        )
      }
      
    }
    else
    {
        // alert("x");
        return(
          <div className="boxes center">
            <div className="boxx">
            <table className="tables">
              <tr>
              <th>Subjects</th>
              </tr>
              {
              back.map((item)=>{
                return(
                  <div key="item">
                    <tr>
                      <td>{item}</td>
                    </tr>
                  </div>
                )
              })
              }
            </table>
            </div>
          </div>
        )
    }
  }

  const Funct = () => {
     return (
      <div className="main center">
      <div className="box center">
        <h2 className="headi">Results</h2>
        <div className="inputs">
          <form onSubmit={handleSubmit} className="forms">
            <select value={el} onChange={event => sete(event.target.value)}>
            <option value="1-1">1-1</option>
            <option value="1-2">1-2</option>
            <option value="2-1">2-1</option>
            <option value="2-2">2-2</option>
            <option value="3-1">3-1</option>
            <option value="3-2">3-2</option>
            <option value="4-1">4-1</option>
            <option value="4-2">4-2</option>
            <option value="backlog">backlogs</option>
            </select>
            <input type="submit" value="Submit" />
          </form>
        </div>
      </div>
      <Resp />
    </div>
     )
  }
  
  return ( v ? <Funct/> : <Navigate to ="/" />)

 
