import React from "react";
import "../css/App.css"
import { Outlet, Link } from "react-router-dom";


function func(){
    var element = document.body;
    var btn = document.getElementById("theme-button");
    if (btn.classList.contains("uil-moon"))
    {btn.classList.add("uil-sun");
    btn.classList.remove("uil-moon");}
    else{
      btn.classList.remove("uil-sun");
      btn.classList.add("uil-moon");
    }
    element.classList.toggle("dark-theme");
};

const Home = () => {
    return (
        <>
        <header className="header" id="header">
        <nav className="nav container">
            <a href="" className="nav__logo">Student Results</a>

            <div className="nav__menu" id="nav-menu">
                <ul className="nav__list grid">
                    <li className="nav__item">
                        <Link className="nav__link active-link" to="/">
                        <i className="uil uil-estate nav__icon"></i>Home
                        </Link>
                    </li>
                    <li className="nav__item">
                        <Link to="/form1" className="nav__link">
                            <i className="uil uil-user nav__icon"></i> Teacher Login
                        </Link>
                    </li>
                    <li className="nav__item">
                        <Link to="/form2" className="nav__link">
                            <i className="uil uil-file-alt nav__icon"></i> Student Login
                        </Link>
                    </li>
                    {/* <li className="nav__item">
                        <a href="#services" className="nav__link">
                            <i className="uil uil-briefcase-alt nav__icon"></i> Services
                        </a>
                    </li> */}
                    {/* <li className="nav__item">
                        <a href="#portfolio" className="nav__link">
                            <i className="uil uil-scenery nav__icon"></i> Portfolio
                        </a>
                    </li>
                    <li className="nav__item">
                        <a href="#contact" className="nav__link">
                            <i className="uil uil-message nav__icon"></i> Contact
                        </a>
                    </li> */}
                </ul>
                <i className="uil uil-times nav__close" id="nav-close"></i>
            </div>

            <div className="nav__btns">

                <button onClick={func} className="btn" ><i className="uil uil-moon change-theme"
                        id="theme-button"></i></button>
                <div className="nav__toggle" id="nav-toggle">
                    <i className="uil uil-apps"></i>
                </div>
            </div>
        </nav>
    </header>
    </>
    )
}

export default Home;
