    const [dat,setdata] = useState([]);
    const [i,seti] = useState(1);

    useEffect(() => {
        const sublist = async() => {
    
            const res = await fetch('http://localhost:3001/sub',{
                method:'POST',
                headers: {
                     'Content-Type' : 'application/json'
                },
                body: JSON.stringify({
                    
                })
            })
    
            const data = await res.json()
            if(data.status === 'ok'){
                console.log("successful");
               console.log(data.data);
               setdata(data.data);
            }
            else{
                alert(data.message);
            }
        }
        sublist();
    });
    

  return (
    <div>
        <h1>Manage Subjects</h1>
        <h2>View Subjects info</h2>
        <table>
            <thead>
                <tr>
                <th>#</th>
                <th>Subject Name</th>
                <th>Subject Code</th>
                <th>Action</th>
                </tr>
            </thead>
            {
                dat.map((item)=>{
                    return(
                      <div key="item">
                      <tr>
                        <td>{i}</td>
                        <td>{item.Subject}</td>
                        <td>{item.Code}</td>
                      </tr>
                      </div>
                    )
                    {serial}
                })
            }
            {/* <Ress da={dat}/> */}
        </table>
    </div>
  )
}

export default ManageSubjects
