import React, { Component } from 'react'
import Select from 'react-select'
import axios from 'axios'

export default class CombinationSubject extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      dep:'',
      sems:'',
      sub:'',
      dept:[],
      sem:[],
    }
  }

 async getOptions(){
    const res = await axios.post('http://localhost:3001/sub')
    const data = res.data.data
    const depths = ['CSE','ECE','EEE','CIVIL','MECH','ECM','IT']
    const sem = ['1-1','1-2','2-1','2-2','3-1','3-2','4-1','4-2']
    const options = data.map(d => ({
      "value" : d.Subject,
      "label" : d.Subject

    }))

    const depts = depths.map(d=>({
        "value":d,
        "label":d
    }))

    const sems = sem.map(d=>({
        "value":d,
        "label":d
    }))



    this.setState({selectOptions: options,dept:depts,sem:sems})

  }


  handleChange(e){
   this.setState({sub:e.value})
  }

  handledept(e){
    this.setState({dep:e.value})
   }

   handlesem(e){
    this.setState({sems:e.value})
   }

  componentDidMount(){
      this.getOptions()
  }

  render() {
    // console.log(this.state.selectOptions)
    return (
      <div>
        <h1>Add Subject Combination</h1>
        <h2>Add Subject Combination</h2>
        
        <Select options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
        <Select options={this.state.dept} onChange={this.handle.bind(this)} />
        <Select options={this.state.sem} onChange={this.handleChange.bind(this)} />
        <p>You have selected <strong>{this.state.name}</strong> whose id is <strong>{this.state.id}</strong></p>
      </div>
    )
  }
}
